import React, { useState } from 'react';
import axios from 'axios';
import './styles.css';

const App = () => {
  const [image, setImage] = useState(null);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setImage(reader.result); // Guarda la imagen en base64
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleSearch = async () => {
    if (!image) {
      alert('Por favor, sube una imagen primero.');
      return;
    }

    setLoading(true);

    try {
      const response = await axios.post('http://localhost:3001/api/search', {
        image: image,
      });

      setResults(response.data.results); // Guarda los resultados
    } catch (error) {
      console.error('Error al buscar productos:', error);
      alert('Hubo un error al buscar productos. Por favor, intenta de nuevo.');
    } finally {
      setLoading(false);
    }
  };

  return (
      <div className="app">
        <h1>Búsqueda Visual de Inditex</h1>
        <input type="file" onChange={handleImageUpload} accept="image/*" />
        <button onClick={handleSearch} disabled={loading}>
          {loading ? 'Buscando...' : 'Buscar'}
        </button>

        <div className="results">
          {results.map((product, index) => (
              <div key={index} className="product">
                <img src={product.image} alt={product.name} />
                <h2>{product.name}</h2>
                <p>{product.description}</p>
                <p>Precio: {product.price} €</p>
              </div>
          ))}
        </div>
      </div>
  );
};

export default App;
